import React, { useState, useEffect, useRef } from 'react';
import { 
  View, 
  TouchableOpacity, 
  SafeAreaView, 
  Text,
  ActivityIndicator,
  Alert,
  Platform
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import { Audio } from 'expo-av';
import * as FileSystem from 'expo-file-system';
import SafeMitraLogo from '../../components/SafeMitraLogo';
import styles from '../styles-part/HotwordDetectionScreenStyles';

// AssemblyAI API key
const ASSEMBLY_API_KEY = '6582380b8a8e49cb81545a91e76c8960';
const HOTWORDS = ['sos', 'help', 'bachaao'];

const HotwordDetectionScreen = () => {
  const navigation = useNavigation();
  const [isListening, setIsListening] = useState(false);
  const [recording, setRecording] = useState(null);
  const [transcript, setTranscript] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const recordingRef = useRef(null);
  const isRecordingRef = useRef(false);

  useEffect(() => {
    return () => {
      stopRecording();
    };
  }, []);

  const sendSOSStatusToBackend = async (isActive) => {
    try {
      if (isActive) {
        console.log('ðŸš¨ SOS TRIGGERED SUCCESSFULLY!');
        Alert.alert('SOS Triggered', 'Emergency alert has been activated!');
      } else {
        console.log('SOS Status: Deactivated');
      }
    } catch (error) {
      console.error('Error updating SOS status:', error);
    }
  };

  async function startRecording() {
    try {
      if (isRecordingRef.current) return;
      
      setIsLoading(true);
      const { status } = await Audio.requestPermissionsAsync();
      if (status !== 'granted') {
        Alert.alert('Permission Required', 'Please grant microphone permission to use this feature');
        return;
      }

      await Audio.setAudioModeAsync({
        allowsRecordingIOS: true,
        playsInSilentModeIOS: true,
      });

      const { recording } = await Audio.Recording.createAsync(
        Audio.RecordingOptionsPresets.HIGH_QUALITY
      );
      
      recordingRef.current = recording;
      setRecording(recording);
      setIsListening(true);
      isRecordingRef.current = true;
      console.log('Recording started');

      // Set up a timer to process the recording
      setTimeout(async () => {
        if (isRecordingRef.current) {
          await processRecording();
          // Start a new recording
          startRecording();
        }
      }, 5000); // Process every 5 seconds

    } catch (err) {
      console.error('Failed to start recording:', err);
      Alert.alert('Error', 'Failed to start recording');
    } finally {
      setIsLoading(false);
    }
  }

  async function stopRecording() {
    try {
      setIsLoading(true);
      isRecordingRef.current = false;
      
      if (recordingRef.current) {
        await recordingRef.current.stopAndUnloadAsync();
        recordingRef.current = null;
      }
      if (recording) {
        await recording.stopAndUnloadAsync();
      }
      
      await sendSOSStatusToBackend(false);
    } catch (err) {
      console.error('Error stopping recording:', err);
    } finally {
      setIsLoading(false);
      setRecording(null);
      setIsListening(false);
    }
  }

  async function processRecording() {
    try {
      if (!recordingRef.current) return;

      const uri = recordingRef.current.getURI();
      console.log('Processing recording from:', uri);

      // Create form data
      const formData = new FormData();
      formData.append('audio', {
        uri: Platform.OS === 'ios' ? uri.replace('file://', '') : uri,
        type: 'audio/m4a',
        name: 'recording.m4a'
      });

      // Upload to AssemblyAI
      const uploadResponse = await fetch('https://api.assemblyai.com/v2/upload', {
        method: 'POST',
        headers: {
          authorization: ASSEMBLY_API_KEY,
        },
        body: formData
      });

      if (!uploadResponse.ok) {
        throw new Error('Upload failed');
      }

      const { upload_url } = await uploadResponse.json();

      // Transcribe
      const transcriptResponse = await fetch('https://api.assemblyai.com/v2/transcript', {
        method: 'POST',
        headers: {
          authorization: ASSEMBLY_API_KEY,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          audio_url: upload_url,
          language_code: 'en'
        }),
      });

      const { id } = await transcriptResponse.json();

      // Poll for results
      let transcriptText = '';
      while (true) {
        await new Promise(resolve => setTimeout(resolve, 1000));
        const resultResponse = await fetch(
          `https://api.assemblyai.com/v2/transcript/${id}`,
          {
            headers: { authorization: ASSEMBLY_API_KEY },
          }
        );
        const result = await resultResponse.json();
        
        if (result.status === 'completed') {
          transcriptText = result.text;
          break;
        } else if (result.status === 'error') {
          throw new Error(result.error);
        }
      }

      if (transcriptText) {
        setTranscript(transcriptText);
        console.log('Transcript:', transcriptText);

        const matched = HOTWORDS.find(word =>
          transcriptText.toLowerCase().includes(word)
        );

        if (matched) {
          console.log('ðŸš¨ SOS Triggered: Hotword Detected â†’', matched);
          await sendSOSStatusToBackend(true);
          navigation.navigate('AutoAlertActive');
        }
      }
    } catch (err) {
      console.error('Error processing recording:', err);
    }
  }

  const toggleHotwordDetection = async () => {
    if (isListening) {
      await stopRecording();
    } else {
      await startRecording();
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity 
          style={styles.backButton}
          onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="#FF3B30" />
        </TouchableOpacity>
        <View style={styles.logoContainer}>
          <SafeMitraLogo color="#FF3B30" size={30} />
          <Text style={styles.logoText}>SafeMitra</Text>
        </View>
      </View>

      {/* Content */}
      <View style={styles.content}>
        {isLoading && <ActivityIndicator size="large" color="#FF3B30" style={styles.loadingIndicator} />}

        <View style={styles.hotwordCard}>
          <View style={styles.statusIndicator}>
            <View style={[
              styles.statusDot,
              { backgroundColor: isListening ? '#4CAF50' : '#FF3B30' }
            ]} />
            <Text style={styles.statusText}>
              {isListening ? 'Status: Listening' : 'Status: Paused'}
            </Text>
            
            <TouchableOpacity 
              style={styles.toggleButton}
              onPress={toggleHotwordDetection}
              disabled={isLoading}
            >
              <Text style={styles.toggleButtonText}>
                {isListening ? 'Stop Recording' : 'Start Recording'}
              </Text>
            </TouchableOpacity>
          </View>

          <View style={styles.mainContent}>
            <View style={styles.iconContainer}>
              <Ionicons name="mic" size={40} color="#FF3B30" />
              <View style={styles.waveformContainer}>
                <View style={[styles.waveform, styles.waveform1]} />
                <View style={[styles.waveform, styles.waveform2]} />
                <View style={[styles.waveform, styles.waveform3]} />
              </View>
            </View>

            <Text style={styles.title}>Voice Hotword Detection</Text>
            <Text style={styles.description}>
              SafeMitra is listening for emergency keywords: "SOS", "Help", "Bachaao"
            </Text>

            {transcript ? (
              <View style={styles.transcriptContainer}>
                <Text style={styles.transcriptLabel}>Last Transcript:</Text>
                <Text style={styles.transcriptText}>{transcript}</Text>
              </View>
            ) : null}
          </View>
        </View>
      </View>
    </SafeAreaView>
  );
};

export default HotwordDetectionScreen;
