import React, { useState, useEffect } from 'react';
import { View, TouchableOpacity, SafeAreaView, Text, ScrollView } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import SafeMitraLogo from '../../components/SafeMitraLogo';
import styles from '../styles-part/AutoAlertActiveScreenStyles';

const AutoAlertActiveScreen = () => {
  const navigation = useNavigation();
  const [countdown, setCountdown] = useState(10);
  const [isAlertActive, setIsAlertActive] = useState(true);
  const [actions, setActions] = useState({
    locationSent: false,
    policeCalled: false,
    evidenceSaved: false,
  });

  useEffect(() => {
    let timer;
    if (isAlertActive && countdown > 0) {
      timer = setInterval(() => {
        setCountdown(prev => prev - 1);
      }, 1000);
    } else if (countdown === 0 && isAlertActive) {
      // Execute emergency actions when countdown reaches 0
      executeEmergencyActions();
    }
    return () => clearInterval(timer);
  }, [countdown, isAlertActive]);

  const executeEmergencyActions = () => {
    // Simulate actions being completed
    setTimeout(() => setActions(prev => ({ ...prev, locationSent: true })), 1000);
    setTimeout(() => setActions(prev => ({ ...prev, policeCalled: true })), 2000);
    setTimeout(() => setActions(prev => ({ ...prev, evidenceSaved: true })), 3000);
  };

  const handleCancelAlert = () => {
    setIsAlertActive(false);
    // Navigate back or to a confirmation screen
    navigation.goBack();
  };

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity 
          style={styles.backButton}
          onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="#FF3B30" />
        </TouchableOpacity>
        <View style={styles.logoContainer}>
          <SafeMitraLogo color="#FF3B30" size={30} />
          <Text style={styles.logoText}>SafeMitra</Text>
        </View>
      </View>

      <ScrollView contentContainerStyle={styles.content}>
        {/* Alert Header */}
        <View style={styles.alertHeader}>
          <View style={styles.alertIconContainer}>
            <Ionicons name="alert-circle" size={40} color="#FF3B30" />
          </View>
          <Text style={styles.alertTitle}>Auto Alert Triggered</Text>
          <Text style={styles.alertMessage}>
            We detected a trained hotword and triggered emergency actions. 
            If this was accidental, you can cancel it within {countdown} seconds.
          </Text>
        </View>

        {/* Timer */}
        <View style={styles.timerContainer}>
          <View style={styles.timerCircle}>
            <Text style={styles.timerText}>{countdown}</Text>
          </View>
        </View>

        {/* Cancel Button */}
        <TouchableOpacity 
          style={styles.cancelButton}
          onPress={handleCancelAlert}>
          <Text style={styles.cancelButtonText}>Cancel Alert</Text>
        </TouchableOpacity>

        {/* Status Section */}
        <View style={styles.statusSection}>
          <Text style={styles.statusTitle}>Emergency Actions</Text>
          
          <View style={styles.statusItem}>
            <Ionicons 
              name={actions.locationSent ? "checkmark-circle" : "time"} 
              size={24} 
              color={actions.locationSent ? "#4CAF50" : "#FF3B30"} 
              style={styles.statusIcon}
            />
            <Text style={styles.statusText}>Location sent to Guardians</Text>
          </View>

          <View style={styles.statusItem}>
            <Ionicons 
              name={actions.policeCalled ? "checkmark-circle" : "time"} 
              size={24} 
              color={actions.policeCalled ? "#4CAF50" : "#FF3B30"} 
              style={styles.statusIcon}
            />
            <Text style={styles.statusText}>Calling Police & NGO</Text>
          </View>

          <View style={styles.statusItem}>
            <Ionicons 
              name={actions.evidenceSaved ? "checkmark-circle" : "time"} 
              size={24} 
              color={actions.evidenceSaved ? "#4CAF50" : "#FF3B30"} 
              style={styles.statusIcon}
            />
            <Text style={styles.statusText}>Evidence Saved to Blockchain</Text>
          </View>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

export default AutoAlertActiveScreen;
