import { StyleSheet } from 'react-native';

export const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FFFFFF',
  },
  scrollContainer: {
    flexGrow: 1,
    padding: 20,
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 30,
  },
  backButton: {
    padding: 10,
  },
  logoContainer: {
    flex: 1,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
  },
  logoText: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#FF3B30',
    marginLeft: 10,
  },
  formContainer: {
    flex: 1,
    alignItems: 'center',
  },
  title: {
    fontSize: 26,
    fontWeight: 'bold',
    color: '#FF3B30',
    marginBottom: 10,
    textAlign: 'center',
  },
  subtitle: {
    fontSize: 16,
    color: '#666666',
    textAlign: 'center',
    marginBottom: 30,
    maxWidth: '80%',
  },
  pinContainer: {
    flexDirection: 'row',
    justifyContent: 'center',
    marginBottom: 20,
  },
  pinInput: {
    width: 50,
    height: 50,
    borderWidth: 1,
    borderColor: '#FF3B30',
    borderRadius: 10,
    backgroundColor: '#F5F5F5',
    marginHorizontal: 8,
    textAlign: 'center',
    fontSize: 20,
    color: '#000000',
  },
  activePinInput: {
    borderColor: '#FF3B30',
    borderWidth: 2,
  },
  hintContainer: {
    width: '100%',
    marginBottom: 20,
  },
  hintLabel: {
    fontSize: 16,
    color: '#666666',
    marginBottom: 8,
  },
  hintInput: {
    width: '100%',
    height: 50,
    backgroundColor: '#F5F5F5',
    borderRadius: 10,
    paddingHorizontal: 15,
    fontSize: 16,
  },
  infoBox: {
    backgroundColor: '#FFF4F4',
    padding: 15,
    borderRadius: 10,
    marginBottom: 20,
    width: '100%',
    flexDirection: 'row',
    alignItems: 'center',
  },
  infoText: {
    color: '#444444',
    fontSize: 14,
    marginLeft: 10,
    flex: 1,
  },
  continueButton: {
    backgroundColor: '#FF3B30',
    width: '100%',
    height: 50,
    borderRadius: 25,
    justifyContent: 'center',
    alignItems: 'center',
    flexDirection: 'row',
    marginBottom: 20,
    shadowColor: '#FF3B30',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  continueButtonText: {
    color: '#FFFFFF',
    fontSize: 16,
    fontWeight: 'bold',
    marginRight: 10,
  },
  securityTips: {
    color: '#666666',
    fontSize: 12,
    textAlign: 'center',
    marginTop: 10,
  },
  validationIcon: {
    position: 'absolute',
    right: 10,
    top: 10,
  },
});
