import React, { useState, useRef } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  SafeAreaView,
  Animated,
  TextInput,
  Alert,
  ActivityIndicator,
  KeyboardAvoidingView,
  Platform,
} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { Ionicons } from '@expo/vector-icons';
import SafeMitraLogo from '../../components/SafeMitraLogo';
import styles from '../styles-part/EvidenceSecureScreenStyles';

/**
 * EvidenceSecureScreen
 * 
 * This screen handles:
 * 1. Displaying the secure status of evidence
 * 2. Providing access to emergency data and logs
 * 3. Managing secure code validation
 * 
 * Backend Integration Points:
 * 1. Secret Code Validation:
 *    - Create an API endpoint for validating the secret code
 *    - Endpoint should be: POST /api/security/validate-code
 *    - Request body: { code: string }
 *    - Response: { isValid: boolean, message?: string }
 * 
 * 2. Emergency Data Access:
 *    - Create an API endpoint for fetching emergency data
 *    - Endpoint should be: GET /api/emergency/data
 *    - Headers: Include authentication token
 *    - Response: { data: EmergencyData, timestamp: string }
 * 
 * 3. Access Logs:
 *    - Create an API endpoint for fetching access logs
 *    - Endpoint should be: GET /api/access-logs
 *    - Headers: Include authentication token
 *    - Response: { logs: AccessLog[] }
 */
const EvidenceSecureScreen = () => {
  const navigation = useNavigation();
  const [isExpanded, setIsExpanded] = useState(false);
  const pulseAnim = new Animated.Value(1);
  
  // Secure Data Access States
  const [code, setCode] = useState(['', '', '', '', '', '']);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [activeInput, setActiveInput] = useState(0);
  const [showSecureAccess, setShowSecureAccess] = useState(false);
  const codeInputRefs = useRef([...Array(6)].map(() => React.createRef()));

  React.useEffect(() => {
    const pulse = Animated.sequence([
      Animated.timing(pulseAnim, {
        toValue: 1.2,
        duration: 1000,
        useNativeDriver: true,
      }),
      Animated.timing(pulseAnim, {
        toValue: 1,
        duration: 1000,
        useNativeDriver: true,
      }),
    ]);

    Animated.loop(pulse).start();
  }, []);

  /**
   * Handles input changes for each digit of the code
   * - Validates that only numbers are entered
   * - Auto-focuses the next input box
   * - Updates the code state
   */
  const handleCodeChange = (value, index) => {
    if (!/^\d*$/.test(value)) return;

    const newCode = [...code];
    newCode[index] = value;
    setCode(newCode);

    if (value !== '') {
      if (index < 5) {
        setActiveInput(index + 1);
        codeInputRefs.current[index + 1].focus();
      }
    }
  };

  /**
   * Handles backspace key press
   * - Moves focus to previous input when backspace is pressed
   * - Clears the previous input value
   */
  const handleCodeKeyPress = (event, index) => {
    if (event.nativeEvent.key === 'Backspace' && index > 0 && !code[index]) {
      setActiveInput(index - 1);
      codeInputRefs.current[index - 1].focus();
      const newCode = [...code];
      newCode[index - 1] = '';
      setCode(newCode);
    }
  };

  /**
   * Handles code submission and validation
   * 
   * Backend Integration:
   * 1. Replace the temporary validation with API call
   * 2. Add proper error handling for API responses
   * 3. Implement rate limiting for failed attempts
   * 4. Add logging for security audit
   */
  const handleSubmit = async () => {
    const codeString = code.join('');
    if (codeString.length !== 6) {
      Alert.alert('Invalid Code', 'Please enter a 6-digit code');
      return;
    }

    setIsLoading(true);
    try {
      // TODO: Replace with actual API call
      // const response = await securityService.validateSecretCode(codeString);
      // if (response.isValid) {
      //   setIsAuthenticated(true);
      //   // Store authentication token for subsequent API calls
      //   await AsyncStorage.setItem('authToken', response.token);
      // } else {
      //   Alert.alert('Access Denied', response.message || 'Incorrect Code. Access Denied.');
      //   setCode(['', '', '', '', '', '']);
      //   setActiveInput(0);
      //   codeInputRefs.current[0].focus();
      // }

      // Temporary validation for development
      if (codeString === '123456') { // Replace with actual stored code
        setIsAuthenticated(true);
      } else {
        Alert.alert('Access Denied', 'Incorrect Code. Access Denied.');
        setCode(['', '', '', '', '', '']);
        setActiveInput(0);
        codeInputRefs.current[0].focus();
      }
    } catch (error) {
      Alert.alert('Error', 'Failed to validate code. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <KeyboardAvoidingView 
        behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
        style={styles.keyboardView}
      >
        {/* Header */}
        <View style={styles.header}>
          <TouchableOpacity
            style={styles.backButton}
            onPress={() => navigation.goBack()}>
            <Ionicons name="arrow-back" size={24} color="#FF3B30" />
          </TouchableOpacity>
          <View style={styles.logoContainer}>
            <SafeMitraLogo color="#FF3B30" size={30} />
            <Text style={styles.logoText}>SafeMitra</Text>
          </View>
        </View>

        <View style={styles.content}>
          {!showSecureAccess ? (
            <>
              {/* Title Section */}
              <View style={styles.titleSection}>
                <Animated.View 
                  style={[
                    styles.shieldIconContainer,
                    { transform: [{ scale: pulseAnim }] }
                  ]}>
                  <Ionicons name="shield-checkmark" size={40} color="#FF3B30" />
                </Animated.View>
                <Text style={styles.title}>Your Data is Locked</Text>
                <Text style={styles.subtitle}>
                  Your voice and location evidence has been encrypted and stored using blockchain technology. Only trusted guardians can access it using your secret code.
                </Text>
              </View>

              {/* Info Card */}
              <View style={styles.infoCard}>
                <View style={styles.infoItem}>
                  <Ionicons
                    name="checkmark-circle"
                    size={24}
                    color="#4CAF50"
                    style={styles.infoIcon}
                  />
                  <Text style={styles.infoText}>Status: Evidence Encrypted</Text>
                </View>
                <View style={styles.infoItem}>
                  <Ionicons
                    name="lock-closed"
                    size={24}
                    color="#FF3B30"
                    style={styles.infoIcon}
                  />
                  <Text style={styles.infoText}>Access: Locked</Text>
                </View>
                <View style={styles.infoItem}>
                  <Ionicons
                    name="cube"
                    size={24}
                    color="#FF3B30"
                    style={styles.infoIcon}
                  />
                  <Text style={styles.infoText}>Storage: Blockchain Confirmed</Text>
                </View>
              </View>

              {/* Blockchain Info Section */}
              <TouchableOpacity
                style={styles.blockchainInfo}
                onPress={() => setIsExpanded(!isExpanded)}>
                <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center' }}>
                  <Text style={styles.blockchainTitle}>What is Blockchain?</Text>
                  <Ionicons
                    name={isExpanded ? "chevron-up" : "chevron-down"}
                    size={24}
                    color="#666666"
                  />
                </View>
                {isExpanded && (
                  <Text style={styles.blockchainText}>
                    Blockchain is a secure, decentralized technology that ensures your data cannot be altered or deleted. Each piece of evidence is stored across multiple secure locations, making it tamper-proof and permanently accessible.
                  </Text>
                )}
              </TouchableOpacity>

              {/* View Logs Button */}
              <TouchableOpacity
                style={styles.viewLogsButton}
                onPress={() => setShowSecureAccess(true)}>
                <Text style={styles.viewLogsButtonText}>View Data Access Logs</Text>
              </TouchableOpacity>
            </>
          ) : (
            <>
              <Text style={styles.title}>Secure Data Access</Text>
              
              {!isAuthenticated ? (
                <View style={styles.formContainer}>
                  <Text style={styles.label}>Enter Secret Code</Text>
                  <View style={styles.pinContainer}>
                    {code.map((digit, index) => (
                      <TextInput
                        key={`code-${index}`}
                        ref={el => codeInputRefs.current[index] = el}
                        style={[
                          styles.pinInput,
                          activeInput === index && styles.activePinInput
                        ]}
                        maxLength={1}
                        keyboardType="number-pad"
                        value={digit}
                        onChangeText={(value) => handleCodeChange(value, index)}
                        onKeyPress={(event) => handleCodeKeyPress(event, index)}
                        onFocus={() => setActiveInput(index)}
                        secureTextEntry
                        editable={!isLoading}
                      />
                    ))}
                  </View>
                  <TouchableOpacity
                    style={[styles.submitButton, { opacity: isLoading ? 0.7 : 1 }]}
                    onPress={handleSubmit}
                    disabled={isLoading}>
                    {isLoading ? (
                      <ActivityIndicator color="#FFFFFF" size="small" />
                    ) : (
                      <Text style={styles.submitButtonText}>Submit</Text>
                    )}
                  </TouchableOpacity>
                </View>
              ) : (
                <View style={styles.optionsContainer}>
                  <TouchableOpacity 
                    style={styles.optionCard}
                    onPress={() => {
                      // TODO: Implement emergency data access
                      // 1. Fetch emergency data from API
                      // 2. Navigate to data view screen
                      // 3. Handle loading and error states
                    }}>
                    <Ionicons name="alert-circle" size={32} color="#FF3B30" />
                    <Text style={styles.optionTitle}>View Emergency Data</Text>
                    <Text style={styles.optionDescription}>Access your emergency evidence and recordings</Text>
                  </TouchableOpacity>

                  <TouchableOpacity 
                    style={styles.optionCard}
                    onPress={() => {
                      // TODO: Implement access logs view
                      // 1. Fetch access logs from API
                      // 2. Navigate to logs view screen
                      // 3. Handle loading and error states
                    }}>
                    <Ionicons name="time" size={32} color="#FF3B30" />
                    <Text style={styles.optionTitle}>View Access Logs</Text>
                    <Text style={styles.optionDescription}>Check who accessed your data and when</Text>
                  </TouchableOpacity>
                </View>
              )}

              <Text style={styles.warningText}>
                For your security, emergency evidence is locked and encrypted.
              </Text>
            </>
          )}
        </View>
      </KeyboardAvoidingView>
    </SafeAreaView>
  );
};

export default EvidenceSecureScreen;
