import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  SafeAreaView,
  ScrollView,
  TouchableOpacity,
  Animated,
} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { Ionicons } from '@expo/vector-icons';
import SafeMitraLogo from '../../components/SafeMitraLogo';

const GuardianAlertSentScreen = () => {
  const navigation = useNavigation();
  const [pulseAnim] = useState(new Animated.Value(1));
  const [guardians, setGuardians] = useState([
    { id: 1, name: 'John Doe', phone: '+91 98765 43210', status: 'Pending' },
    { id: 2, name: 'Jane Smith', phone: '+91 98765 43211', status: 'Viewed' },
    { id: 3, name: 'Mike Johnson', phone: '+91 98765 43212', status: 'Responded' },
  ]);

  useEffect(() => {
    const pulseAnimation = Animated.loop(
      Animated.sequence([
        Animated.timing(pulseAnim, {
          toValue: 1.2,
          duration: 1000,
          useNativeDriver: true,
        }),
        Animated.timing(pulseAnim, {
          toValue: 1,
          duration: 1000,
          useNativeDriver: true,
        }),
      ])
    );

    pulseAnimation.start();

    return () => pulseAnimation.stop();
  }, []);

  const getStatusIcon = (status) => {
    switch (status) {
      case 'Pending':
        return 'â³';
      case 'Viewed':
        return 'ðŸ‘ï¸';
      case 'Responded':
        return 'âœ…';
      default:
        return '';
    }
  };

  const getStatusCount = () => {
    const viewed = guardians.filter(g => g.status === 'Viewed').length;
    const responded = guardians.filter(g => g.status === 'Responded').length;
    return { viewed, responded };
  };

  const { viewed, responded } = getStatusCount();

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity 
          style={styles.backButton}
          onPress={() => navigation.goBack()}
        >
          <Ionicons name="arrow-back" size={24} color="#FF3B30" />
        </TouchableOpacity>
        <View style={styles.logoContainer}>
          <SafeMitraLogo color="#FF3B30" size={30} />
          <Text style={styles.logoText}>SafeMitra</Text>
        </View>
        <View style={styles.rightPlaceholder} />
      </View>

      <ScrollView contentContainerStyle={styles.scrollContent}>
        <View style={styles.content}>
          {/* Alert Icon */}
          <Animated.View style={[styles.alertIconContainer, { transform: [{ scale: pulseAnim }] }]}>
            <Ionicons name="notifications" size={60} color="#FFFFFF" />
          </Animated.View>

          {/* Title */}
          <Text style={styles.title}>Your Guardians Have Been Alerted</Text>

          {/* Description */}
          <Text style={styles.description}>
            All listed guardians have received your SOS alert along with your live location. 
            The alert will vibrate continuously on their devices until one of them responds.
          </Text>

          {/* Status Card */}
          <View style={styles.statusCard}>
            {guardians.map((guardian, index) => (
              <View key={guardian.id}>
                <View style={styles.guardianItem}>
                  <View style={styles.guardianInfo}>
                    <Text style={styles.guardianName}>{guardian.name}</Text>
                    <Text style={styles.guardianPhone}>{guardian.phone}</Text>
                  </View>
                  <Text style={styles.statusText}>
                    {getStatusIcon(guardian.status)} {guardian.status}
                  </Text>
                </View>
                {index < guardians.length - 1 && <View style={styles.divider} />}
              </View>
            ))}
          </View>

          {/* Response Tracker */}
          <View style={styles.trackerContainer}>
            <Text style={styles.trackerTitle}>Response Status</Text>
            <View style={styles.trackerStats}>
              <View style={styles.statItem}>
                <Text style={styles.statNumber}>{viewed}</Text>
                <Text style={styles.statLabel}>Viewed</Text>
              </View>
              <View style={styles.statItem}>
                <Text style={styles.statNumber}>{responded}</Text>
                <Text style={styles.statLabel}>Responded</Text>
              </View>
            </View>
          </View>

          {/* Notification Text */}
          <Text style={styles.notificationText}>
            You'll be notified once any guardian stops the alert
          </Text>

          {/* Progress Bar */}
          <View style={styles.progressBarContainer}>
            <Animated.View 
              style={[
                styles.progressBar,
                { width: `${(responded / guardians.length) * 100}%` }
              ]} 
            />
          </View>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FFFFFF',
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    paddingHorizontal: 20,
    paddingVertical: 15,
    borderBottomWidth: 1,
    borderBottomColor: '#EEEEEE',
  },
  backButton: {
    padding: 5,
  },
  logoContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  logoText: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#FF3B30',
    marginLeft: 10,
  },
  rightPlaceholder: {
    width: 24,
  },
  scrollContent: {
    flexGrow: 1,
  },
  content: {
    padding: 20,
    alignItems: 'center',
  },
  alertIconContainer: {
    width: 100,
    height: 100,
    borderRadius: 50,
    backgroundColor: '#FF3B30',
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#FF3B30',
    textAlign: 'center',
    marginBottom: 15,
  },
  description: {
    fontSize: 16,
    color: '#666666',
    textAlign: 'center',
    marginBottom: 30,
    lineHeight: 24,
  },
  statusCard: {
    width: '100%',
    backgroundColor: '#F8F8F8',
    borderRadius: 12,
    padding: 15,
    marginBottom: 20,
  },
  guardianItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingVertical: 10,
  },
  guardianInfo: {
    flex: 1,
  },
  guardianName: {
    fontSize: 16,
    fontWeight: '600',
    color: '#111111',
    marginBottom: 4,
  },
  guardianPhone: {
    fontSize: 14,
    color: '#666666',
  },
  statusText: {
    fontSize: 14,
    color: '#666666',
  },
  divider: {
    height: 1,
    backgroundColor: '#EEEEEE',
    marginVertical: 5,
  },
  trackerContainer: {
    width: '100%',
    marginBottom: 20,
  },
  trackerTitle: {
    fontSize: 18,
    fontWeight: '600',
    color: '#111111',
    marginBottom: 15,
    textAlign: 'center',
  },
  trackerStats: {
    flexDirection: 'row',
    justifyContent: 'space-around',
  },
  statItem: {
    alignItems: 'center',
  },
  statNumber: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#FF3B30',
    marginBottom: 5,
  },
  statLabel: {
    fontSize: 14,
    color: '#666666',
  },
  notificationText: {
    fontSize: 14,
    color: '#666666',
    textAlign: 'center',
    marginBottom: 20,
  },
  progressBarContainer: {
    width: '100%',
    height: 4,
    backgroundColor: '#EEEEEE',
    borderRadius: 2,
    overflow: 'hidden',
  },
  progressBar: {
    height: '100%',
    backgroundColor: '#FF3B30',
    borderRadius: 2,
  },
});

export default GuardianAlertSentScreen;
