                  value={fullName}
                  onChangeText={setFullName}
                />
              </View>

              {/* Create PIN Input */}
              <View style={styles.inputContainer}>
                <Text style={styles.label}>Create PIN</Text>
                <View style={styles.pinInput}>
                  <TextInput
                    style={[styles.input, { flex: 1 }]}
                    placeholder="****"
                    secureTextEntry={!showPin}
                    value={pin}
                    onChangeText={setPin}
                    maxLength={4}
                    keyboardType="numeric"
                  />
                  <TouchableOpacity
                    style={styles.eyeIcon}
                    onPress={() => setShowPin(!showPin)}
                  >
                    <Ionicons
                      name={showPin ? 'eye-off' : 'eye'}
                      size={24}
                      color="#666"
                    />
                  </TouchableOpacity>
                </View>
              </View>

              {/* Confirm PIN Input */}
              <View style={styles.inputContainer}>
                <Text style={styles.label}>Confirm PIN</Text>
                <View style={styles.pinInput}>
                  <TextInput
                    style={[styles.input, { flex: 1 }]}
                    placeholder="****"
                    secureTextEntry={!showConfirmPin}
                    value={confirmPin}
                    onChangeText={setConfirmPin}
                    maxLength={4}
                    keyboardType="numeric"
                  />
                  <TouchableOpacity
                    style={styles.eyeIcon}
                    onPress={() => setShowConfirmPin(!showConfirmPin)}
                  >
                    <Ionicons
                      name={showConfirmPin ? 'eye-off' : 'eye'}
                      size={24}
                      color="#666"
                    />
                  </TouchableOpacity>
                </View>
              </View>

              {/* Terms and Conditions */}
              <View style={styles.termsContainer}>
                <TouchableOpacity
                  style={styles.checkbox}
                  onPress={() => setAcceptedTerms(!acceptedTerms)}
                >
                  <Ionicons
                    name={acceptedTerms ? 'checkbox' : 'square-outline'}
                    size={24}
                    color="#d32f2f"
                  />
                </TouchableOpacity>
                <Text style={styles.termsText}>
                  I agree to the{' '}
                  <Text style={styles.termsLink}>Privacy Policy</Text> &{' '}
                  <Text style={styles.termsLink}>Terms of Use</Text>
                </Text>
              </View>

              {/* Register Button */}
              <TouchableOpacity
                style={[
                  styles.registerButton,
                ]}
                onPress={handleRegister}
              >
                <>
                  <Ionicons name="shield-checkmark" size={24} color="#FFFFFF" />
                  <Text style={styles.registerButtonText}>Register Securely</Text>
                </>
              </TouchableOpacity>

              {/* Encryption Notice */}
              <View style={styles.encryptedContainer}>
                <Ionicons name="lock-closed" size={16} color="#999" />
                <Text style={styles.encryptedText}>
                  Your information is end-to-end encrypted
                </Text>
              </View>
            </>
          )}

          {/* Login Link */}
          <View style={styles.loginContainer}>
            <Text style={styles.loginText}>Already have an account? </Text>
            <TouchableOpacity onPress={() => navigation.navigate('Login')}>
              <Text style={styles.loginLink}>Login â†’</Text>
            </TouchableOpacity>
          </View>
        </View>
      </ScrollView>
    </KeyboardAvoidingView>
  );
};

