import React, { useState, useEffect, useRef } from 'react';
import { 
  View, 
  TouchableOpacity, 
  SafeAreaView, 
  Text,
  ActivityIndicator,
  Alert
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import { Audio } from 'expo-av';
import * as FileSystem from 'expo-file-system';
import SafeMitraLogo from '../../components/SafeMitraLogo';
import styles from '../styles-part/HotwordDetectionScreenStyles';

// Replace with your actual AssemblyAI API key
const ASSEMBLY_API_KEY = 'YOUR_ASSEMBLY_AI_API_KEY_HERE';
const HOTWORDS = ['sos', 'help', 'bachaao'];

const HotwordDetectionScreen = () => {
  const navigation = useNavigation();
  const [isListening, setIsListening] = useState(false);
  const [recording, setRecording] = useState(null);
  const [transcript, setTranscript] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const recordingRef = useRef(null);

  async function startRecording() {
    try {
      setIsLoading(true);
      const { status } = await Audio.requestPermissionsAsync();
      if (status !== 'granted') {
        Alert.alert('Permission Required', 'Please grant microphone permission to use this feature');
        return;
      }

      await Audio.setAudioModeAsync({
        allowsRecordingIOS: true,
        playsInSilentModeIOS: true,
      });

      const { recording } = await Audio.Recording.createAsync(
        Audio.RecordingOptionsPresets.HIGH_QUALITY
      );
      recordingRef.current = recording;
      setRecording(recording);
      setIsListening(true);
      console.log('Recording started');
    } catch (err) {
      console.error('Failed to start recording', err);
      Alert.alert('Error', 'Failed to start recording');
    } finally {
      setIsLoading(false);
    }
  }

  async function stopRecording() {
    try {
      setIsLoading(true);
      console.log('Stopping recording...');
      if (!recordingRef.current) return;

      await recordingRef.current.stopAndUnloadAsync();
      const uri = recordingRef.current.getURI();
      console.log('Recording stopped and stored at', uri);

      const base64Audio = await FileSystem.readAsStringAsync(uri, {
        encoding: FileSystem.EncodingType.Base64,
      });

      const audioBuffer = Buffer.from(base64Audio, 'base64');
      const uploadUrl = await uploadToAssembly(audioBuffer);

      if (uploadUrl) {
        const transcriptText = await transcribeWithAssembly(uploadUrl);
        setTranscript(transcriptText);
        console.log('Transcript:', transcriptText);

        const matched = HOTWORDS.find(word =>
          transcriptText.toLowerCase().includes(word)
        );

        if (matched) {
          console.log('ðŸš¨ SOS Triggered: Hotword Detected â†’', matched);
          navigation.navigate('AutoAlertActive');
        } else {
          console.log('No hotword detected.');
        }
      }
    } catch (err) {
      console.error('Error in stopRecording:', err);
      Alert.alert('Error', 'Failed to process recording');
    } finally {
      setIsLoading(false);
      setRecording(null);
      setIsListening(false);
    }
  }

  async function uploadToAssembly(audioBuffer) {
    try {
      const response = await fetch('https://api.assemblyai.com/v2/upload', {
        method: 'POST',
        headers: {
          authorization: ASSEMBLY_API_KEY,
          'Transfer-Encoding': 'chunked',
        },
        body: audioBuffer,
      });

      const data = await response.json();
      return data.upload_url;
    } catch (error) {
      console.error('Upload failed:', error);
      Alert.alert('Error', 'Failed to upload audio');
      return null;
    }
  }

  async function transcribeWithAssembly(audioUrl) {
    try {
      const response = await fetch('https://api.assemblyai.com/v2/transcript', {
        method: 'POST',
        headers: {
          authorization: ASSEMBLY_API_KEY,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ audio_url: audioUrl }),
      });

      const data = await response.json();
      const transcriptId = data.id;

      let completed = false;
      let transcriptText = '';
      while (!completed) {
        await new Promise(res => setTimeout(res, 3000));
        const pollingRes = await fetch(
          `https://api.assemblyai.com/v2/transcript/${transcriptId}`,
          {
            headers: { authorization: ASSEMBLY_API_KEY },
          }
        );
        const pollingData = await pollingRes.json();
        if (pollingData.status === 'completed') {
          completed = true;
          transcriptText = pollingData.text;
        } else if (pollingData.status === 'error') {
          throw new Error(pollingData.error);
        }
      }

      return transcriptText;
    } catch (error) {
      console.error('Transcription failed:', error);
      Alert.alert('Error', 'Failed to transcribe audio');
      return '';
    }
  }

  useEffect(() => {
    return () => {
      if (recording) {
        stopRecording();
      }
    };
  }, []);

  const toggleHotwordDetection = async () => {
    if (isListening) {
      await stopRecording();
    } else {
      await startRecording();
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity 
          style={styles.backButton}
          onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="#FF3B30" />
        </TouchableOpacity>
        <View style={styles.logoContainer}>
          <SafeMitraLogo color="#FF3B30" size={30} />
          <Text style={styles.logoText}>SafeMitra</Text>
        </View>
      </View>

      {/* Content */}
      <View style={styles.content}>
        {/* Loading Indicator */}
        {isLoading && <ActivityIndicator size="large" color="#FF3B30" style={styles.loadingIndicator} />}

        <View style={styles.hotwordCard}>
          {/* Status Indicator */}
          <View style={styles.statusIndicator}>
            <View style={[
              styles.statusDot,
              { backgroundColor: isListening ? '#4CAF50' : '#FF3B30' }
            ]} />
            <Text style={styles.statusText}>
              {isListening ? 'Status: Listening' : 'Status: Paused'}
            </Text>
            
            {/* Toggle Button */}
            <TouchableOpacity 
              style={styles.toggleButton}
              onPress={toggleHotwordDetection}
              disabled={isLoading}
            >
              <Text style={styles.toggleButtonText}>
                {isListening ? 'Stop Recording' : 'Start Recording'}
              </Text>
            </TouchableOpacity>
          </View>

          {/* Main Content */}
          <View style={styles.mainContent}>
            {/* Icon */}
            <View style={styles.iconContainer}>
              <Ionicons name="mic" size={40} color="#FF3B30" />
              <View style={styles.waveformContainer}>
                <View style={[styles.waveform, styles.waveform1]} />
                <View style={[styles.waveform, styles.waveform2]} />
                <View style={[styles.waveform, styles.waveform3]} />
              </View>
            </View>

            {/* Title */}
            <Text style={styles.title}>Voice Hotword Detection</Text>

            {/* Description */}
            <Text style={styles.description}>
              SafeMitra is listening for emergency keywords: "SOS", "Help", "Bachaao"
            </Text>

            {/* Transcript Display */}
            {transcript ? (
              <View style={styles.transcriptContainer}>
                <Text style={styles.transcriptLabel}>Last Transcript:</Text>
                <Text style={styles.transcriptText}>{transcript}</Text>
              </View>
            ) : null}
          </View>
        </View>
      </View>
    </SafeAreaView>
  );
};

export default HotwordDetectionScreen;
