import React from 'react';
import { View, Text } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import styles from '../styles-part/LiveLocationStatusCardStyles';

const LiveLocationStatusCard = ({ isActive }) => {
  return (
    <View style={styles.locationStatusCard}>
      <View style={styles.locationStatusHeader}>
        <Ionicons name="location" size={24} color="#FF3B30" />
        <Text style={styles.locationStatusTitle}>Live Location</Text>
      </View>
      <View style={styles.locationStatusContent}>
        <View style={styles.locationStatusIndicator}>
          <View style={[
            styles.locationStatusDot,
            { backgroundColor: isActive ? '#4CAF50' : '#FF3B30' }
          ]} />
          <Text style={styles.locationStatusText}>
            {isActive ? 'Active' : 'Inactive'}
          </Text>
        </View>
        <Text style={styles.locationStatusSubtext}>
          {isActive 
            ? 'Your location is being shared with trusted contacts'
            : 'Location sharing is currently disabled'}
        </Text>
      </View>
    </View>
  );
};

export default LiveLocationStatusCard;
