import React, { useState } from 'react';
import { View, TouchableOpacity, SafeAreaView, Text } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import styles from '../styles-part/LiveLocationStatusScreenStyles';

const LiveLocationStatusScreen = () => {
  const navigation = useNavigation();
  const [isLocationActive, setIsLocationActive] = useState(true);

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity 
          style={styles.backButton}
          onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="#FF3B30" />
        </TouchableOpacity>
        <View style={styles.headerTitle}>
          <Ionicons name="location" size={24} color="#FF3B30" />
          <Text style={styles.title}>Live Location Status</Text>
        </View>
      </View>

      {/* Content */}
      <View style={styles.content}>
        <View style={styles.locationStatusCard}>
          <View style={styles.locationStatusHeader}>
            <Ionicons name="location" size={24} color="#FF3B30" />
            <Text style={styles.locationStatusTitle}>Live Location</Text>
          </View>
          <View style={styles.locationStatusContent}>
            <View style={styles.locationStatusIndicator}>
              <View style={[
                styles.locationStatusDot,
                { backgroundColor: isLocationActive ? '#4CAF50' : '#FF3B30' }
              ]} />
              <Text style={styles.locationStatusText}>
                {isLocationActive ? 'Active' : 'Inactive'}
              </Text>
            </View>
            <Text style={styles.locationStatusSubtext}>
              {isLocationActive 
                ? 'Your location is being shared with trusted contacts'
                : 'Location sharing is currently disabled'}
            </Text>
          </View>
        </View>
      </View>
    </SafeAreaView>
  );
};

export default LiveLocationStatusScreen;
