    shadowOpacity: 0.2,
    shadowRadius: 3,
  },
  sendOtpText: {
    color: '#FFFFFF',
    fontSize: 16,
    fontWeight: '600',
  },
  verifyButton: {
    backgroundColor: '#1A237E',
    borderRadius: 12,
    padding: 16,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: 24,
    elevation: 3,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.2,
    shadowRadius: 3,
  },
  verifyText: {
    color: '#FFFFFF',
    fontSize: 16,
    fontWeight: '600',
    marginRight: 8,
  },
  securityContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: 24,
    backgroundColor: '#FFF5F5',
    padding: 12,
    borderRadius: 12,
  },
  securityText: {
    fontSize: 14,
    color: '#666666',
    marginLeft: 8,
  },
  supportButton: {
    alignItems: 'center',
    marginTop: 8,
  },
  supportText: {
    fontSize: 14,
    color: '#d32f2f',
    fontWeight: '600',
    textDecorationLine: 'underline',
  },
});
