import { StyleSheet, Dimensions } from 'react-native';

const { width, height } = Dimensions.get('window');

export const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FF3B30',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: 20,
  },
  contentContainer: {
    alignItems: 'center',
    marginTop: height * 0.15, // Position content higher up
  },
  logoContainer: {
    marginBottom: 40,
  },
  shieldContainer: {
    alignItems: 'center',
  },
  shield: {
    width: 80,
    height: 100,
    backgroundColor: '#FFFFFF',
    borderRadius: 10,
    position: 'relative',
    transform: [{ scaleX: 1.1 }],
    // Create shield shape with border radius
    borderTopLeftRadius: 40,
    borderTopRightRadius: 40,
    justifyContent: 'center',
    alignItems: 'center',
  },
  femaleSymbol: {
    alignItems: 'center',
  },
  circle: {
    width: 24,
    height: 24,
    borderRadius: 12,
    borderWidth: 3,
    borderColor: '#FF3B30',
  },
  cross: {
    position: 'relative',
    marginTop: -12,
  },
  verticalLine: {
    width: 3,
    height: 24,
    backgroundColor: '#FF3B30',
    position: 'absolute',
    left: '50%',
    marginLeft: -1.5,
  },
  horizontalLine: {
    width: 16,
    height: 3,
    backgroundColor: '#FF3B30',
    position: 'absolute',
    top: '50%',
    left: '50%',
    marginLeft: -8,
    marginTop: -1.5,
  },
  title: {
    color: '#FFFFFF',
    fontSize: 36,
    fontWeight: '600',
    textAlign: 'center',
    marginBottom: 8,
    fontFamily: 'System',
  },
  subtitle: {
    color: '#FFFFFF',
    fontSize: 20,
    textAlign: 'center',
    opacity: 0.9,
    fontFamily: 'System',
  },
  button: {
    backgroundColor: '#FFFFFF',
    width: width - 40,
    height: 56,
    borderRadius: 30,
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 40,
  },
  buttonText: {
    color: '#FF3B30',
    fontSize: 20,
    fontWeight: '500',
    fontFamily: 'System',
  },
});
