import React, { useState, useEffect, useRef } from 'react';
import { 
  View, 
  TouchableOpacity, 
  SafeAreaView, 
  Text,
  ActivityIndicator,
  Alert,
  Platform
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import { Audio } from 'expo-av';
import * as FileSystem from 'expo-file-system';
import NetInfo from '@react-native-community/netinfo';
import SafeMitraLogo from '../../components/SafeMitraLogo';
import styles from '../styles-part/HotwordDetectionScreenStyles';

// TODO: Move to secure storage or environment variables
const ASSEMBLY_API_KEY = '6582380b8a8e49cb81545a91e76c8960';
const HOTWORDS = ['sos', 'help', 'bachaao'];
const COOLDOWN_PERIOD = 15000; // 15 seconds cooldown between triggers
const MAX_RETRY_ATTEMPTS = 5;
const INITIAL_RETRY_DELAY = 1000; // 1 second

const HotwordDetectionScreen = () => {
  const navigation = useNavigation();
  const [isListening, setIsListening] = useState(false);
  const [recording, setRecording] = useState(null);
  const [transcript, setTranscript] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isOnline, setIsOnline] = useState(true);
  const [pendingRecordings, setPendingRecordings] = useState([]);
  
  // Refs for state management
  const recordingRef = useRef(null);
  const isRecordingRef = useRef(false);
  const processingRef = useRef(false);
  const lastTriggeredRef = useRef(0);
  const debounceTimeoutRef = useRef(null);
  const retryCountRef = useRef(0);

  useEffect(() => {
    // Check network connectivity
    const checkConnectivity = async () => {
      try {
        const netInfo = await NetInfo.fetch();
        setIsOnline(Boolean(netInfo.isConnected));
        if (netInfo.isConnected && pendingRecordings.length > 0) {
          processPendingRecordings();
        }
      } catch (error) {
        console.log('Network check failed:', error);
        setIsOnline(false);
      }
    };

    checkConnectivity();
    const unsubscribe = NetInfo.addEventListener(state => {
      setIsOnline(Boolean(state.isConnected));
      if (state.isConnected && pendingRecordings.length > 0) {
        processPendingRecordings();
      }
    });

    // Cleanup function
    return () => {
      unsubscribe();
      stopRecording();
      Audio.setAudioModeAsync({ 
        allowsRecordingIOS: false,
        playsInSilentModeIOS: false 
      });
      if (debounceTimeoutRef.current) {
        clearTimeout(debounceTimeoutRef.current);
      }
    };
  }, [pendingRecordings]);

  const processPendingRecordings = async () => {
    for (const recording of pendingRecordings) {
      try {
        await processRecording(recording.uri);
        // Remove processed recording from pending list
        setPendingRecordings(prev => prev.filter(r => r.uri !== recording.uri));
      } catch (error) {
        console.error('Error processing pending recording:', error);
      }
    }
  };

  const handleSOSStatus = (isActive) => {
    const now = Date.now();
    if (isActive && now - lastTriggeredRef.current < COOLDOWN_PERIOD) {
      console.log('SOS trigger on cooldown');
      return;
    }
    
    lastTriggeredRef.current = now;
    
    if (isActive) {
      console.log('ðŸš¨ SOS TRIGGERED SUCCESSFULLY!');
      Alert.alert(
        'SOS Triggered',
        'Emergency alert has been activated!',
        [
          {
            text: 'OK',
            onPress: () => navigation.navigate('AutoAlertActive')
          }
        ]
      );
    } else {
      console.log('SOS Status: Deactivated');
      Alert.alert('SOS Deactivated', 'Emergency alert has been stopped.');
    }
  };

  async function startRecording() {
    try {
      if (isRecordingRef.current || processingRef.current) {
        console.log('Recording or processing already in progress');
        return;
      }
      
      if (!isOnline) {
        Alert.alert(
          'No Internet Connection',
          'Recording will be stored and processed when online.'
        );
      }

      setIsLoading(true);
      const { status } = await Audio.requestPermissionsAsync();
      if (status !== 'granted') {
        Alert.alert('Permission Required', 'Please grant microphone permission to use this feature');
        return;
      }

      await Audio.setAudioModeAsync({
        allowsRecordingIOS: true,
        playsInSilentModeIOS: true,
        staysActiveInBackground: true,
        shouldDuckAndroid: true,
        playThroughEarpieceAndroid: false
      });

      const { recording } = await Audio.Recording.createAsync(
        Audio.RecordingOptionsPresets.HIGH_QUALITY
      );
      
      recordingRef.current = recording;
      setRecording(recording);
      setIsListening(true);
      isRecordingRef.current = true;
      console.log('Recording started');

      // Process recording after 5 seconds
      setTimeout(async () => {
        if (isRecordingRef.current) {
          await processRecording();
        }
      }, 5000);

    } catch (err) {
      console.error('Failed to start recording:', err);
      Alert.alert('Error', 'Failed to start recording');
    } finally {
      setIsLoading(false);
    }
  }

  async function stopRecording() {
    try {
      setIsLoading(true);
      isRecordingRef.current = false;
      processingRef.current = false;
      
      if (recordingRef.current) {
        await recordingRef.current.stopAndUnloadAsync();
        recordingRef.current = null;
      }
      if (recording) {
        await recording.stopAndUnloadAsync();
      }
      
      handleSOSStatus(false);
    } catch (err) {
      console.error('Error stopping recording:', err);
    } finally {
      setIsLoading(false);
      setRecording(null);
      setIsListening(false);
    }
  }

  async function processRecording(uri = null) {
    if (processingRef.current) {
      console.log('Already processing a recording');
      return;
    }

    try {
      processingRef.current = true;
      
      if (!recordingRef.current && !uri) {
        processingRef.current = false;
        return;
      }

      const recordingUri = uri || recordingRef.current.getURI();
      console.log('Processing recording from:', recordingUri);

      if (!isOnline) {
        // Store recording for later processing
        const pendingPath = `${FileSystem.documentDirectory}pending_recording_${Date.now()}.m4a`;
        await FileSystem.copyAsync({ from: recordingUri, to: pendingPath });
        setPendingRecordings(prev => [...prev, { uri: pendingPath, timestamp: Date.now() }]);
        processingRef.current = false;
        return;
      }

      // Stop current recording before processing
      if (recordingRef.current) {
        await recordingRef.current.stopAndUnloadAsync();
        isRecordingRef.current = false;
      }

      // Create form data
      const formData = new FormData();
      formData.append('audio', {
        uri: Platform.OS === 'ios' ? recordingUri.replace('file://', '') : recordingUri,
        type: 'audio/m4a',
        name: 'recording.m4a'
      });

      // Upload to AssemblyAI with exponential backoff
      let uploadResponse;
      retryCountRef.current = 0;
      
      while (retryCountRef.current < MAX_RETRY_ATTEMPTS) {
        try {
          uploadResponse = await fetch('https://api.assemblyai.com/v2/upload', {
            method: 'POST',
            headers: {
              authorization: ASSEMBLY_API_KEY,
            },
            body: formData
          });
          break;
        } catch (error) {
          retryCountRef.current++;
          if (retryCountRef.current === MAX_RETRY_ATTEMPTS) throw error;
          await new Promise(resolve => 
            setTimeout(resolve, INITIAL_RETRY_DELAY * Math.pow(2, retryCountRef.current))
          );
        }
      }

      if (!uploadResponse.ok) {
        throw new Error('Upload failed');
      }

      const { upload_url } = await uploadResponse.json();

      // Transcribe with exponential backoff
      let transcriptResponse;
      retryCountRef.current = 0;
      
      while (retryCountRef.current < MAX_RETRY_ATTEMPTS) {
        try {
          transcriptResponse = await fetch('https://api.assemblyai.com/v2/transcript', {
            method: 'POST',
            headers: {
              authorization: ASSEMBLY_API_KEY,
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ 
              audio_url: upload_url,
              language_code: 'en'
            }),
          });
          break;
        } catch (error) {
          retryCountRef.current++;
          if (retryCountRef.current === MAX_RETRY_ATTEMPTS) throw error;
          await new Promise(resolve => 
            setTimeout(resolve, INITIAL_RETRY_DELAY * Math.pow(2, retryCountRef.current))
          );
        }
      }

      const { id } = await transcriptResponse.json();

      // Poll for results with exponential backoff
      let transcriptText = '';
      let attempts = 0;
      const maxAttempts = 10;

      while (attempts < maxAttempts) {
        await new Promise(resolve => 
          setTimeout(resolve, INITIAL_RETRY_DELAY * Math.pow(2, attempts))
        );
        
        const resultResponse = await fetch(
          `https://api.assemblyai.com/v2/transcript/${id}`,
          {
            headers: { authorization: ASSEMBLY_API_KEY },
          }
        );
        const result = await resultResponse.json();
        
        if (result.status === 'completed') {
          transcriptText = result.text;
          break;
        } else if (result.status === 'error') {
          throw new Error(result.error);
        }
        attempts++;
      }

      if (transcriptText) {
        // Save transcript to text file
        const transcriptPath = `${FileSystem.documentDirectory}transcript_${Date.now()}.txt`;
        await FileSystem.writeAsStringAsync(transcriptPath, transcriptText);
        console.log('Transcript saved to:', transcriptPath);

        setTranscript(transcriptText);
        console.log('Transcript:', transcriptText);

        // Check for hotwords
        const matched = HOTWORDS.find(word =>
          transcriptText.toLowerCase().includes(word)
        );

        if (matched) {
          console.log('ðŸš¨ SOS Triggered: Hotword Detected â†’', matched);
          handleSOSStatus(true);
        }
      }

      // Start new recording after processing
      if (!isRecordingRef.current) {
        startRecording();
      }

    } catch (err) {
      console.error('Error processing recording:', err);
      if (err.message.includes('timeout')) {
        Alert.alert('Connection Timeout', 'Please check your internet connection and try again.');
      }
    } finally {
      processingRef.current = false;
      retryCountRef.current = 0;
    }
  }

  const toggleHotwordDetection = async () => {
    if (debounceTimeoutRef.current) return;
    
    debounceTimeoutRef.current = setTimeout(() => {
      debounceTimeoutRef.current = null;
    }, 2000);

    if (isListening) {
      await stopRecording();
    } else {
      await startRecording();
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.header}>
        <TouchableOpacity 
          style={styles.backButton}
          onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="#FF3B30" />
        </TouchableOpacity>
        <View style={styles.logoContainer}>
          <SafeMitraLogo color="#FF3B30" size={30} />
          <Text style={styles.logoText}>SafeMitra</Text>
        </View>
      </View>

      <View style={styles.content}>
        {isLoading && <ActivityIndicator size="large" color="#FF3B30" style={styles.loadingIndicator} />}

        <View style={styles.hotwordCard}>
          <View style={styles.statusIndicator}>
            <View style={[
              styles.statusDot,
              { backgroundColor: isListening ? '#4CAF50' : '#FF3B30' }
            ]} />
            <Text style={styles.statusText}>
              {isListening ? 'Status: Listening' : 'Status: Paused'}
            </Text>
            
            <TouchableOpacity 
              style={styles.toggleButton}
              onPress={toggleHotwordDetection}
              disabled={isLoading || Boolean(debounceTimeoutRef.current)}
            >
              <Text style={styles.toggleButtonText}>
                {isListening ? 'Stop Recording' : 'Start Recording'}
              </Text>
            </TouchableOpacity>
          </View>

          <View style={styles.mainContent}>
            <View style={styles.iconContainer}>
              <Ionicons name="mic" size={40} color="#FF3B30" />
              <View style={styles.waveformContainer}>
                <View style={[styles.waveform, styles.waveform1]} />
                <View style={[styles.waveform, styles.waveform2]} />
                <View style={[styles.waveform, styles.waveform3]} />
              </View>
            </View>

            <Text style={styles.title}>Voice Hotword Detection</Text>
            <Text style={styles.description}>
              SafeMitra is listening for emergency keywords: "SOS", "Help", "Bachaao"
            </Text>

            {!isOnline && (
              <Text style={[styles.description, { color: '#FF3B30' }]}>
                No internet connection. Recordings will be processed when online.
              </Text>
            )}

            {pendingRecordings.length > 0 && (
              <Text style={[styles.description, { color: '#FFA500' }]}>
                {pendingRecordings.length} recording(s) waiting to be processed
              </Text>
            )}

            {transcript ? (
              <View style={styles.transcriptContainer}>
                <Text style={styles.transcriptLabel}>Last Transcript:</Text>
                <Text 
                  numberOfLines={3} 
                  ellipsizeMode="tail" 
                  style={styles.transcriptText}
                >
                  {transcript}
                </Text>
              </View>
            ) : null}
          </View>
        </View>
      </View>
    </SafeAreaView>
  );
};

export default HotwordDetectionScreen;
