import React from 'react';
import { View, Text, TextInput, TouchableOpacity, SafeAreaView } from 'react-native';
import { styles } from '../styles-part/MobileRegistrationScreenStyles';

const MobileRegistrationScreen = ({ navigation }) => {
  const [phoneNumber, setPhoneNumber] = React.useState('');

  const handleContinue = () => {
    // Handle phone number verification
    if (phoneNumber.length === 10) {
      // Navigate to OTP verification or next screen
      // navigation.navigate('OTPVerification', { phoneNumber });
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.contentContainer}>
        <Text style={styles.title}>Enter Mobile Number</Text>
        <Text style={styles.subtitle}>We will send you a verification code</Text>
        
        <View style={styles.inputContainer}>
          <Text style={styles.prefix}>+91</Text>
          <TextInput
            style={styles.input}
            placeholder="Enter your mobile number"
            placeholderTextColor="#999"
            keyboardType="number-pad"
            maxLength={10}
            value={phoneNumber}
            onChangeText={setPhoneNumber}
          />
        </View>

        <TouchableOpacity 
          style={[
            styles.button,
            { opacity: phoneNumber.length === 10 ? 1 : 0.7 }
          ]}
          onPress={handleContinue}
          disabled={phoneNumber.length !== 10}
          activeOpacity={0.8}
        >
          <Text style={styles.buttonText}>Continue</Text>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
};

export default MobileRegistrationScreen;
