import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  SafeAreaView,
  Alert,
  KeyboardAvoidingView,
  Platform,
} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { Ionicons } from '@expo/vector-icons';
import SafeMitraLogo from '../../components/SafeMitraLogo';
import styles from '../styles-part/SecureDataAccessScreenStyles';

const SecureDataAccessScreen = () => {
  const navigation = useNavigation();
  const [secretCode, setSecretCode] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleSubmit = () => {
    // TODO: Replace with actual secret code validation
    if (secretCode === '1234') { // Temporary hardcoded code for testing
      setIsAuthenticated(true);
    } else {
      Alert.alert('Access Denied', 'Incorrect Code. Access Denied.');
      setSecretCode('');
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <KeyboardAvoidingView 
        behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
        style={styles.keyboardView}
      >
        {/* Header */}
        <View style={styles.header}>
          <TouchableOpacity
            style={styles.backButton}
            onPress={() => navigation.goBack()}>
            <Ionicons name="arrow-back" size={24} color="#FF3B30" />
          </TouchableOpacity>
          <View style={styles.logoContainer}>
            <SafeMitraLogo color="#FF3B30" size={30} />
            <Text style={styles.logoText}>SafeMitra</Text>
          </View>
        </View>

        <View style={styles.content}>
          <Text style={styles.title}>Secure Data Access</Text>
          
          {!isAuthenticated ? (
            <View style={styles.formContainer}>
              <Text style={styles.label}>Enter Secret Code</Text>
              <TextInput
                style={styles.input}
                value={secretCode}
                onChangeText={setSecretCode}
                secureTextEntry
                placeholder="Enter your secret code"
                placeholderTextColor="#999"
                keyboardType="number-pad"
                maxLength={4}
              />
              <TouchableOpacity
                style={styles.submitButton}
                onPress={handleSubmit}>
                <Text style={styles.submitButtonText}>Submit</Text>
              </TouchableOpacity>
            </View>
          ) : (
            <View style={styles.optionsContainer}>
              <TouchableOpacity style={styles.optionCard}>
                <Ionicons name="alert-circle" size={32} color="#FF3B30" />
                <Text style={styles.optionTitle}>View Emergency Data</Text>
                <Text style={styles.optionDescription}>Access your emergency evidence and recordings</Text>
              </TouchableOpacity>

              <TouchableOpacity style={styles.optionCard}>
                <Ionicons name="time" size={32} color="#FF3B30" />
                <Text style={styles.optionTitle}>View Access Logs</Text>
                <Text style={styles.optionDescription}>Check who accessed your data and when</Text>
              </TouchableOpacity>
            </View>
          )}

          <Text style={styles.warningText}>
            For your security, emergency evidence is locked and encrypted.
          </Text>
        </View>
      </KeyboardAvoidingView>
    </SafeAreaView>
  );
};

export default SecureDataAccessScreen;
